mapper.select:
SELECT
DATE(o.orders_date) AS saleDate,
SUM(od.goods_count) AS totalSales
FROM orders_detail od
JOIN orders o ON od.orders_no = o.orders_no
WHERE od.goods_id = #{goods_id}

    AND o.orders_date >= #{startDate}


    AND o.orders_date  <= #{endDate} 

GROUP BY DATE(o.orders_date)
ORDER BY o.orders_date

SELECT DATE(o.orders_date) AS saleDate, SUM(od.goods_count) AS totalSales FROM orders_detail od JOIN orders o ON od.orders_no = o.orders_no JOIN goods g ON od.goods_id = g.goods_id WHERE g.goods_name LIKE CONCAT('%', #{goods_name}, '%')
AND o.orders_date >= #{startDate}
AND o.orders_date <= #{endDate}
GROUP BY DATE(o.orders_date) ORDER BY o.orders_date
